import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Header from './Header';
import Sidebar from './Sidebar';
import crud from '../conexiones/crud';
import swal from 'sweetalert';

const Admin = () => {

  const navigate = useNavigate();

  useEffect(() => {
    const autenticarUsuario = async () => {
      const token = localStorage.getItem("token")
      //console.log(token);
      if (!token) {
        navigate("/login");
      }
    }
    autenticarUsuario();
  }, [navigate]); // []hacen que solo se ejecute una vez el useEffect

  const [categoria, setCategorias] = useState([]);

  const cargarCategorias = async () => {
    const response = await crud.GET(`/api/categoria`);
    //console.log(response);
    setCategorias(response.categoria);
  }

  useEffect(() => {
    cargarCategorias();
  }, [])

  const borrarCategoria = async (idCategoria) => {
    console.log(idCategoria)
    const response = await crud.DELETE(`/api/categoria/${idCategoria}`);
    const mensaje = response.msg;
    if (mensaje === "categoria eliminada") {
      const mensaje = "categoria eliminada";
        swal({
          title: 'Sucess',
          text: mensaje,
          icon: 'success',
          buttons: {
            confirm: {
              text: 'OK',
              value: true,
              visible: true,
              className: 'btn btn-primary',
              closeModal: true
            }
          }

        })
        
    } 
    cargarCategorias();
  
  }

  return (
    <>
      <Header />
      <div className='md:flex md:min-h-screen'>
        <Sidebar />
        <main className='flex-1'>
          <h1 className="inline bg-gradient-to-r from-indigo-200 via-violet-700 to-indigo-200 bg-clip-text font-display text-5xl tracking-tight text-transparent">
            Listado Categorias
          </h1>
          <table className='table table-bordered'>
            <thead className='bg-white'>
              <tr>
                <th style={{ width: '10%' }}>Imagen</th>
                <th style={{ width: '75%' }}>Nombre</th>
                <th style={{ width: '15%' }}>Opciones</th>
              </tr>
            </thead>

            <tbody className='bg-white'>
              {
                categoria.map(
                  item =>
                    <tr key={item._id}>
                      <td><img src={item.imagen}></img></td>
                      <td>{item.nombre}</td>
                      <td>
                      </td>

                      <td>
                        <Link
                        to={`/home-productos/${item._id}`}>
                        
                        crear producto</Link>&nbsp;&nbsp;
                        <Link 
                        to={`/actualizar-categoria/${item._id}`}
                        >Editar</Link>&nbsp;&nbsp;
                        <button
                          onClick = {()=>borrarCategoria(item._id)}>
                          Eliminar</button>
                      </td>
                    </tr>
                )


              }

            </tbody>

          </table>
        </main>
      </div>


    </>
  );

}

export default Admin;
